import React from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';

import { fetchCollectionsStartAsync } from '../../redux/shop/shop.actions';

import CollectionsOverviewContainer from '../../components/collections-overview/collections-overview.component';
import CollectionPageContainer from '../collection/collection.container';

const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {

  unsubscribeFromSnapshot = null;

  componentDidMount() {

    const { fetchCollectionsStartAsync, selectIsCollectionFetching } = this.props;

    fetchCollectionsStartAsync();

  }

  render() {

    const { match, selectIsCollectionsLoaded } = this.props;

    return (
      <div className='shop-page'>
        <Route
          exact
          path = { `${match.path}` }
          component = { CollectionsOverviewContainer }
        />
        <Route
          path={`${match.path}/:collectionId`}
          render={props => (
            <CollectionPageWithSpinner isLoading={!selectIsCollectionsLoaded} {...props} />
          )}
        />
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  selectIsCollectionsLoaded: selectIsCollectionsLoaded
});

const mapDispatchToProps = dispatch => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
});

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ShopPage);
